import assert from 'assert';
import sortFixedLengthTuples from '../core/sortFixedLengthTuples';
import sortArbitraryTuples from '../core/sortArbitraryTuples';

const sort = (k, M, tuples) => {
	assert(Number.isInteger(k));
	assert(Number.isInteger(M));
	console.debug('sort', k, tuples);
	if (k === 0) {
		return tuples;
	}

	const N = tuples.length;
	if (N <= 1) {
		return tuples;
	}

	const output = new Array(N);
	if (k >= 1) {
		return sortFixedLengthTuples(k, M, tuples, output, 0, N);
	}

	return sortArbitraryTuples(M, tuples, output);
};

export default sort;

import sortUint32 from './sortUint32';

// http://www.codercorner.com/RadixSortRevisited.htm
// http://stereopsis.com/radix.html
const floatFlip = (f) => {
	const mask = -(f >>> 31) | 0x80000000;
	return f ^ mask;
};

const iFloatFlip = (f) => {
	const mask = ((f >>> 31) - 1) | 0x80000000;
	return f ^ mask;
};

const sortFloat32 = (array) => {
	// TODO avoid copying when passed a Float32Array
	// but be careful to take into account byteOffset, byteLength, and length
	const buffer = Float32Array.from(array).buffer;
	const view = new Uint32Array(buffer);
	const N = array.length;
	for (let i = 0; i < N; ++i) view[i] = floatFlip(view[i]);
	const output = Uint32Array.from(sortUint32(view));
	for (let i = 0; i < N; ++i) output[i] = iFloatFlip(output[i]);
	return new Float32Array(output.buffer);
};

export default sortFloat32;

import sortUint16 from './sortUint16';

const sortInt16 = (array) => {
	const shift = -(2 ** 15);
	// TODO avoid copying back and forth
	const data = Array.prototype.map.call(array, (x) => x - shift);
	const output = sortUint16(data);
	return Array.prototype.map.call(output, (x) => x + shift);
};

export default sortInt16;

import sortUint32 from './sortUint32';

const sortInt32 = (array) => {
	const shift = -(2 ** 31);
	// TODO avoid copying back and forth
	const data = Array.prototype.map.call(array, (x) => x - shift);
	const output = sortUint32(data);
	return Array.prototype.map.call(output, (x) => x + shift);
};

export default sortInt32;

import sortUint8 from './sortUint8';

const sortInt8 = (array) => {
	const shift = -(2 ** 7);
	// TODO avoid copying back and forth
	const data = Array.prototype.map.call(array, (x) => x - shift);
	const output = sortUint8(data);
	return Array.prototype.map.call(output, (x) => x + shift);
};

export default sortInt8;

import sort from './sort';

const sortUint16 = (array) => {
	const k = 2; // TODO make it depend on array.length
	const M = 2 ** 8;
	// TODO avoid copying back and forth
	const tuples = Array.prototype.map.call(array, (x) => [
		(x & 0xff00) >>> 8,
		(x & 0xff) >>> 0
	]);
	const output = sort(k, M, tuples);
	return Array.prototype.map.call(output, ([c, d]) => ((c << 8) | d) >>> 0);
};

export default sortUint16;

import sort from './sort';

const sortUint32 = (array) => {
	const k = 4; // TODO make it depend on array.length
	const M = 2 ** 8; // TODO another good option is k = 3 M = 2**11
	// TODO avoid copying back and forth
	const tuples = Array.prototype.map.call(array, (x) => [
		(x & 0xff000000) >>> 24,
		(x & 0xff0000) >>> 16,
		(x & 0xff00) >>> 8,
		(x & 0xff) >>> 0
	]);
	const output = sort(k, M, tuples);
	return Array.prototype.map.call(
		output,
		([a, b, c, d]) => ((a << 24) | (b << 16) | (c << 8) | d) >>> 0
	);
};

export default sortUint32;

import sort from './sort';

const sortUint8 = (array) => {
	const k = 1; // TODO make it depend on array.length ?
	const M = 2 ** 8;
	// TODO avoid copying back and forth
	const tuples = Array.prototype.map.call(array, (x) => [(x & 0xff) >>> 0]);
	const output = sort(k, M, tuples);
	return Array.prototype.map.call(output, ([d]) => d >>> 0);
};

export default sortUint8;

import assert from 'assert';

/**
 * O(M) time where M=j-i.
 */
const accumulate = (array, i, j) => {
	assert(i >= 0 && j <= array.length);
	for (++i; i < j; ++i) {
		array[i] += array[i - 1];
	}
};

export default accumulate;

// TODO check that this is the type that makes sense to hold counts
const alloc = (n) => new Int32Array(n);
export default alloc;

import assert from 'assert';

const compose = (sigma, tau) => {
	console.debug('compose', sigma, tau);
	const N = sigma.length;
	assert(sigma.length === tau.length);
	for (let i = 0; i < N; ++i) {
		tau[i] = sigma[tau[i]];
	}
};

export default compose;

import assert from 'assert';
import histogram from './histogram';
import accumulate from './accumulate';

/**
 * O(M+N) time where N=j-i and M = ch.length - offset.
 */
const cumulativeHistogram = (array, i, j, ch, offset) => {
	assert(Number.isInteger(offset));
	assert(i >= 0 && i <= j && j <= array.length);
	histogram(array, i, j, ch, offset); // O(N)
	accumulate(ch, offset, ch.length); // O(M)
};

export default cumulativeHistogram;

import alloc from './alloc';
import load from './load';

export default function* digits(tuples, i, j, s, t) {
	const buffer = alloc(j - i);
	for (--t; s <= t; --t) {
		// O(kN) where k = t - s
		load(t, tuples, i, j, buffer);
		yield buffer;
	}
}

import assert from 'assert';

/**
 * O(N) time where N = j - i;
 */
const histogram = (array, i, j, output, offset) => {
	assert(Number.isInteger(offset));
	assert(i >= 0 && j <= array.length);
	for (; i < j; ++i) {
		const key = offset + array[i];
		assert(key >= 0 && key < output.length);
		++output[key];
	}
};

export default histogram;

import assert from 'assert';

const load = (s, tuples, i, j, buffer) => {
	assert(buffer.length >= j - i);
	for (let pt = 0; i < j; ++i) {
		const tuple = tuples[i];
		assert(s >= 0 && s < tuple.length);
		// Assert(0 <= tuple[s] && tuple[s] < M);
		buffer[pt++] = tuple[s];
	}
};

export default load;

const loadLengths = (tuples, i, N, buffer) => {
	let maxLength = 0;
	for (; i < N; ++i) {
		const tuple = tuples[i];
		buffer[i] = tuple.length;
		maxLength = Math.max(maxLength, tuple.length);
	}

	return maxLength + 1;
};

export default loadLengths;

import assert from 'assert';

const permute = (permutation, array, ai, aj, output, oi) => {
	console.debug('permute', permutation, array, ai, aj, output, oi);
	assert(Number.isInteger(ai));
	assert(Number.isInteger(aj));
	assert(Number.isInteger(oi));
	const N = aj - ai; // Only used in assert
	const oj = oi + N; // Only used in assert
	assert(ai >= 0 && aj <= array.length);
	assert(permutation.length >= N);
	assert(oi >= 0 && oj <= output.length);
	for (let i = 0; i < N; ++i) {
		assert(permutation[i] >= 0 && permutation[i] < N);
		output[oi + permutation[i]] = array[ai + i];
	}
};

export default permute;

import assert from 'assert';

const permuteInPlace = (permutation, array) => {
	assert(permutation.length === array.length);
	const N = permutation.length;
	for (let i = 0; i < N; ++i) {
		// Exhaust all cycles
		let j = permutation[i];
		while (i !== j) {
			let temporary = array[j];
			array[j] = array[i];
			array[i] = temporary;
			temporary = j;
			j = permutation[j];
			permutation[j] = temporary;
		}
	}
};

export default permuteInPlace;

import assert from 'assert';

/**
 * O(n) time.
 *
 * @param {Array} ch The cumulative histogram of the input.
 * @param {Array} array The input array to sort.
 * @param {Number} i Inclusive left boundary of the array.
 * @param {Number} j Non-inclusive right boundary of the array.
 * @param {Array} permutation The output permutation.
 */
const rank = (ch, array, i, j, permutation) => {
	assert(i >= 0 && j <= array.length);
	assert(i >= 0 && j <= permutation.length);
	for (; i < j; ++i) {
		assert(array[i] >= 0 && array[i] < ch.length);
		assert(ch[array[i]] >= 0 && ch[array[i]] < permutation.length);
		permutation[i] = ch[array[i]]++;
	}
};

export default rank;

import fill from './fill';
const reset = (array, i, j) => fill(array, 0, i, j);
export default reset;

import permute from './permute';
import sortTuplesByLength from './sortTuplesByLength';
import sortFixedLengthTuples from './sortFixedLengthTuples';

const sortArbitraryTuples = (M, tuples, output) => {
	const N = tuples.length;
	const [ch, permutation] = sortTuplesByLength(tuples);
	permute(permutation, tuples, 0, N, output, 0); // O(N)
	const groups = ch.length - 1;
	console.debug('sortArbitraryTuples', ch, groups);
	for (let g = 1; g < groups; ++g) {
		const i = ch[g - 1];
		const j = ch[g];
		sortFixedLengthTuples(g, M, output, tuples, i, j); // TODO this modifies the input array
	}

	return tuples;
};

export default sortArbitraryTuples;

import assert from 'assert';
import alloc from './alloc';
import load from './load';
import digits from './digits';
import stable from './stable';
import permute from './permute';

/**
 * O(kM + kN) time where k is the number of digits per tuple, M is the radix, and
 * N = j - i is the number of tuples. The product kN corresponds to the input size.
 */
const sortFixedLengthTuples = (k, M, tuples, output, i, j) => {
	console.debug('sortFixedLengthTuples', k, M, tuples, output, i, j);
	assert(k >= 1);
	const N = j - i;
	assert(N >= 2);
	const first = alloc(N);
	load(k - 1, tuples, i, j, first);
	const rest = digits(tuples, i, j, 0, k - 1);
	const permutation = stable(M, first, rest); // O(kN + kM)
	console.debug(permutation, tuples);
	permute(permutation, tuples, i, j, output, i); // O(N)
	return output;
};

export default sortFixedLengthTuples;

import assert from 'assert';
import alloc from './alloc';
import zeros from './zeros';
import cumulativeHistogram from './cumulativeHistogram';
import rank from './rank';

import loadLengths from './loadLengths';

/**
 * O(M + N) time where M is the maximum length of a tuple, and
 * N is the number of tuples.
 */
const sortTuplesByLength = (tuples) => {
	console.debug('sortTuplesByLength', tuples);
	assert(tuples !== undefined);
	const N = tuples.length;
	const lengths = alloc(N);
	const M = loadLengths(tuples, 0, N, lengths);
	const ch = zeros(M + 1); // O(M)
	cumulativeHistogram(lengths, 0, N, ch, 1); // O(N)
	console.debug('sortTuplesByLength step', lengths, ch);
	assert(ch[0] === 0);
	const permutation = alloc(N);
	rank(ch, lengths, 0, lengths.length, permutation); // O(N)
	return [ch, permutation];
};

export default sortTuplesByLength;

import assert from 'assert';
import {_identity} from '@aureooms/js-permutation';
import alloc from './alloc';
import zeros from './zeros';
import reset from './reset';
import cumulativeHistogram from './cumulativeHistogram';
import rank from './rank';
import permute from './permute';
import compose from './compose';

/**
 * O(kM + kN) time where k is the number of arrays, M is the radix, and
 * N is the length of each array. The product kN corresponds to the input size.
 */
const stable = (M, current, rest) => {
	console.debug('stable', M, current, rest);
	assert(current !== undefined);
	const N = current.length;
	const permutation1 = alloc(N);
	_identity(permutation1, N); // TODO Not needed if rest is empty (k=1)
	const ch = zeros(M + 1); // O(M)
	// eslint-disable-next-line no-constant-condition
	while (true) {
		// Repeated k times.
		cumulativeHistogram(current, 0, N, ch, 1); // O(N)
		console.debug('stable step', current, ch);
		assert(ch[0] === 0);
		const permutation2 = alloc(N);
		rank(ch, current, 0, current.length, permutation2); // O(N)
		compose(permutation2, permutation1);
		const {value: next, done} = rest.next();
		if (done) {
			return permutation1;
		}

		assert(next.length === N);
		permute(permutation1, next, 0, N, current, 0);
		reset(ch); // O(M)
	}
};

export default stable;

